function App() {
  const course = {
    id: 1,
    name: 'Half Stack application development',
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10,
        id: 1
      },
      {
        name: 'Using props to pass data',
        exercises: 7,
        id: 2
      },
      {
        name: 'State of a component',
        exercises: 14,
        id: 3
      }
    ]
  };

  return (
    <Course course={course} />
  );
}

const Course =({ course }) => {

  //const {name, parts} = course;

  return (
    <div style={{'textAlign': 'center'}}>
      <Header course={course.name} />
      <Content parts={course.parts} />
      <Total parts={course.parts} />
    </div>
  );
}

const Header = (props) => {
  return (
    <h1>{props.course}</h1>
  );
};

const Content = ({ parts }) =>{
  const partList = parts.map(part => <Part key={part.id} part={part.name} exercises={part.exercises} />);

  return(
    <div>
      {partList}
    </div>
  );
};

const Part = (props) => {
  return(
    <p>
      {props.part} {props.exercises}
    </p>
  );
}

const Total = ({ parts }) => {
  // const getTotal = () => {
  //   let total = 0;
  //   parts.forEach(part => {
  //     total += part.exercises;
  //   });
  //   return total;
  // };
  const exerciseCounts = parts.map(part => part.exercises)
  console.log(exerciseCounts)
  const total = exerciseCounts.reduce((sum, currentPart) =>  {
    console.log(sum);
    return sum + currentPart.exercises;
  });

  return(
    <h3>total of {total} exercises</h3> // fancy solution to exercise 1.4
  );
};

export default App;
